job.command:

  cd ${SUBMITTINGDIRECTORY}  
  set Name = `basename $PWD`
#  setenv NODEBUG yes
  setenv STARFPE NO
#  setup 64b
#  starver .DEV2
  echo $INPUTFILE0 | grep ':'
  if (! $?) then
    set rung  = `echo $INPUTFILE0 | awk -F\: '{print $1}'`
    set f     = `echo $INPUTFILE0 | awk -F\: '{print $2}'`
    set N     = `echo $INPUTFILE0 | awk -F\: '{print $3}'`
    set name  = `echo $INPUTFILE0 | awk -F\: '{print $4}'`
  else
    exit 1;
  endif
  set name = ""
  foreach name (D02KmTsq D0K0s2KmTsq D0K0s2pimTsq D0KpimTsq DK0spimTsq DsK0sK0spimTsq DsK0sKmTsq DsKKpimTsq Lc3pimTsq LcKppimTsq 20Muons) 
    echo $Name | grep $name
    if (! $?) break;
  end
  echo "name = $name";
  if ($name == "") exit 1;

  set chain = "MC.2016a,istSlowSim,StiCA,-hitfilt,KFVertex,StiHftC,beamLine,-geantOut,evout,VFMCE,noRunco,noHistos,noTags,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,"${name};
  set sdt = "";
  set VMCE = "";
  echo $Name | grep -i real
  if (! $?) set sdt = ",sdt20160301";
  echo $Name | grep -i 20Muons
  if ( $? ) set VMCE = ",VMCE";
  set Chain = ${chain}${VMCE}${sdt}",Rung."${rung}
  set base = ${name}_${rung};
  set file = ${base}.MuDst.root
  set logFile  = ${base}B.log
  echo "${file}"
  if ( ! -r ${file}) then
    root.exe -q -b -x 'bfc.C('${f}','${N}',"'${Chain}'",0,"'${file}'")' >& ${logFile}
  endif
