job.command:

  cd ${SUBMITTINGDIRECTORY}  
  set Name = `basename $PWD`
#  setenv NODEBUG yes
#  setenv STARFPE NO
#  setup 64b
#  starver .DEV2
  echo $INPUTFILE0 | grep ':'
  if (! $?) then
    set rung  = `echo $INPUTFILE0 | awk -F\: '{print $1}'`
    set f     = `echo $INPUTFILE0 | awk -F\: '{print $2}'`
    set N     = `echo $INPUTFILE0 | awk -F\: '{print $3}'`
    set name  = `echo $INPUTFILE0 | awk -F\: '{print $4}'`
  else
    exit 1;
  endif
  set name = ""
  foreach name (LcKppimTsq Lc3pimTsq D0KpimTsq D0K0s2pimTsq DsKKpimTsq 20Muons) 
    echo $Name | grep $name
    if (! $?) break;
  end
  echo "name = $name";
  if ($name == "") exit 1;
  set chain = "MC.2016a,StiCA,-hitfilt,KFVertex,StiHftC,beamLine,-geantOut,evout,VFMCE,noRunco,noHistos,noTags,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,"${name};
  echo $Name | grep 20Muons
  if (! $?) set chain = "MC.2016a,StiCA,-hitfilt,KFVertex,StiHftC,beamLine,-geantOut,evout,noRunco,noHistos,noTags,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,"${name};
  set sdt = "";
  echo $Name | grep -i real
  if (! $?) set sdt = ",sdt20160301";
#  set name = "LcKppi";
  set Chain = ${chain}${sdt}",Rung."${rung}
  set base = ${name}_${rung};
  set file = ${base}.MuDst.root
  set logFile  = ${base}B.log
  echo "${file}"
  if ( ! -r ${file}) then
    root.exe -q -b -x 'bfc.C('${f}','${N}',"'${Chain}'",0,"'${file}'")' >& ${logFile}
  endif
