job.command:

  cd ${SUBMITTINGDIRECTORY}  
  set Name = `basename $PWD`
  setenv NODEBUG yes
  setenv STARFPE NO
  setup 64b
#  starver TFG19e
  echo $INPUTFILE0 | grep ':'
  if (! $?) then
    set rung  = `echo $INPUTFILE0 | awk -F\: '{print $1}'`
    set f     = `echo $INPUTFILE0 | awk -F\: '{print $2}'`
    set N     = `echo $INPUTFILE0 | awk -F\: '{print $3}'`
#   set name  = `echo $INPUTFILE0 | awk -F\: '{print $4}'`
  else
    exit 1;
  endif
  set name = ""
  foreach name (K_S0 Lambda0Z200cm Lambda0Z200cmFlat  Lambda0_bar Lambda0 Xi_bar Xi  Omega_bar Omega HyperTriton)
    echo $Name | grep $name
    if (! $?) break;
#    if ($name == $Name) break;
  end
  echo "name = $name";
  if ($name == "") exit 1;
  set noiTPClu = ""
  echo $Name | grep 'noiTPClu' 
  if (! $?) set noiTPClu = ",noiTPClu";
#  setenv StarEndMakerShell
# set chain = "MC.2019a,P2019a,btof,BEmcChkStat,CorrY,OSpaceZ2,OGridLeakFull,-evout,NoHistos,noTags,noRunco,Stx,KFVertex,VFMinuitX,picoWrite,PicoVtxVpdOrDefault,-geantOut,-evout,-useXgeom,"${name};
  set chain = "MC.2018,vmc,VMCAlignment,NoiTPCLu,btof,BEmcChkStat,CorrY,OSpaceZ2,OGridLeakFull,-evout,NoHistos,noTags,noRunco,StiCA,StiKFVertex,picoWrite,PicoVtxVpdOrDefault,-geantOut,-evout,-useXgeom,"${name}${noiTPClu};
  set sdt = ",sdt20180426.135000"
  set VFMCE = ",VFMCE";
  set Chain = ${chain}${VFMCE}${sdt}",Rung."${rung}
  set base = ${name}_${rung}_${f}_${N};
  set file = ${base}.MuDst.root
  set tag  = ${base}.tags.root
  set logFile  = ${base}B.log
  echo "${file}"
  if ( ! -r ${file}) then
    root.exe -q -b -x 'bfc.C('${f}','${N}',"'${Chain}'",0,"'${file}'","'${tag}'")' >& ${logFile}
  endif
