job.command:

  cd ${SUBMITTINGDIRECTORY}  
  setenv NODEBUG yes
  setup 64b
  setenv STARFPE NO
  starver .DEV2
  set muDst = ${INPUTFILE0}
  set base = `basename ${muDst} .MuDst.root`;
  set daq  = /star/data03/daq/2016/126/17126033/${base}.daq
  if (! -r ${daq}) exit 1;
#  set name = "Lc3pi";
  set file = ${base}.MuDst.root
  set logFile  = ${base}B.log
  echo "${file}"
  if ( ! -r ${file}) then
    root.exe -q -b -x 'bfcMixerVMC.C(10000,"Vmc,Lc3pi,VMCAlignment","'${daq}'","'${muDst}'")' >& ${logFile}
  endif
