job.command:
       cd ${SUBMITTINGDIRECTORY}
setenv STARFPE NO
setenv NODEBUG yes
setup 64b
starver TFG18d
set filelist = `cat $FILELIST | awk '{print $1}'`
echo "file list = ${filelist}"
set NL = `echo ${filelist} | wc -l`
echo "NL = ${NL}"
if (${NL} == 0) exit 0;
set tag = `basename $PWD`_${JOBINDEX}
set log = ${tag}B.log
set femtoRoot = ${tag}.femtoDst.root
if (-r ${log}) exit 0;
root.exe -q -b -x 'femtoDst.C(1e8,"'${FILELIST}'","'${femtoRoot}'")' >& ${log}
