job.command:

  cd ${SUBMITTINGDIRECTORY}  
  setenv NODEBUG yes
  setup 64b
  setup gcc621
  setenv STARFPE NO
  starver TFG17j
  set file = $INPUTFILE0;
#  set rung = `echo $file | awk -F\: '{print $1}'`
#  set f    = `echo $file | awk -F\: '{print $2}'`
#  set l    = `echo $file | awk -F\: '{print $3}'`
  set name = `basename ${file} .gener.root`;
  echo "${name}"
  set logName  = ${name}B.log
  set MuDstF   = ${name}.MuDst.root
  setenv StarEndMakerShell
  set sdt = "";
  set Name = `basename $PWD`
  echo $Name | grep -i Ideal
  if ($?) set sdt = ",sdt20160301";
  set fitter = ""
  set chain = "genIn,MC.2016a,istSlowSim,-bbcSim,StiCA,-hitfilt,StiHftC,-geantOut,-evout,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,NoHistos,noTags,noRunco,ZCut5cm"${fitter}${sdt}",noRunco,noHistos";
#  set chain = "genIn,MC.2016a,istSlowSim,-bbcSim,StiCA,KFVertex,-hitfilt,StiHftC,-geantOut,-evout,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,NoHistos,noTags,noRunco,ZCut5cm,TObjTable"${fitter},${sdt}",noRunco,noHistos";
  echo "chain = ${chain}"

  if (! -r ${MuDstF} ) then
#    root.exe -q -b -x 'bfc.C(1000,"genIn,MC.2016a,StiCA,-hitfilt,KFVertex,StiHftC,geantOut,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,NoHistos,noTags,noRunco","'${file}'")' >& ${logName}
#    root.exe -q -b -x 'bfc.C(1000,"genIn,MC.2016a,-bbcSim,StiCA,-hitfilt,KFVertex,StiHftC,geantOut,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,NoHistos,noTags,noRunco,sdt20160504.180732","'${file}'")' >& ${logName}
#    root.exe -q -b -x 'bfc.C(1000,"genIn,MC.2016a,istSlowSim,-bbcSim,StiCA,-hitfilt,StiHftC,geantOut,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,NoHistos,noTags,noRunco,sdt20160511,ZCut5cm","'${file}'")' >& ${logName}
#    root.exe -q -b -x 'bfc.C(1000,"genIn,MC.2016a,istSlowSim,-bbcSim,StiCA,KFVertex,-hitfilt,StiHftC,-geantOut,-evout,vmc,VMCAlignment,CorrX,OSpaceZ2,OGridLeak3D,-useXgeom,NoHistos,noTags,noRunco,sdt20160511,ZCut5cm,TObjTable","'${file}'")' >& ${logName}
    root.exe -q -b -x 'bfc.C(1000,"'${chain}'","'${file}'")' >& ${logName}
  endif
