job.command:
cd ${SUBMITTINGDIRECTORY}
setenv STARFPE NO
set evtsFile = $INPUTFILE0;
echo "${evtsFile}"
set filel    = `basename $evtsFile .daq`
set output  = ${filel}.root
if (! -r ${evtsFile}) exit 1;
set logFile  = ${filel}B.log
if ( -r $logFile) exit 0;
if ( -r $output) exit 0;
root.exe -q -b -x 'TpcPrompt.C(999999,"'${evtsFile}'","'$output'")' >& ${logFile}
