job.command:

  source ~/.tcshrc
  cd ${SUBMITTINGDIRECTORY}  
  starver .DEV2 
  setup gcc451
  set evtsFile = $INPUTFILE0;
  set file     = `basename $evtsFile .MuDst`
  echo "${evtsFile}"
  if (! -r ${evtsFile}) exit 1;
  set logFile  = ${file}B.log
  set f=`find . -maxdepth 1 -type f -name "${file}*.root"`
  if ("$f" == "") then
#    root4star -q -b 'TpcRS.C(4000,"P2011a,TpcRS,Tree,evout,-l3onl,-emcDY2,-fpd,-ftpc,Corr4,OSpaceZ2,OGridLeakFull,-hitfilt","'${evtsFile}'","Bichsel,PhysicsOff")' >& ${logFile}
    root4star -q -b 'TpcRS.C(4000,"y2011,TpcRS","'${evtsFile}'","Bichsel,PhysicsOff")' >& ${logFile}
  endif
