job.command:

  cd ${SUBMITTINGDIRECTORY}  
  setenv NODEBUG yes
  starver .DEV2 
  set file = $INPUTFILE0;
  set rung = `echo $file | awk -F\_ '{print $2}'`
  echo "${file}"
  set logFile  = ${file}B.log
  set f=`find . -maxdepth 1 -type f -name "*${file}*.root"`
  if ("$f" == "") then
    root4star -q -b 'TpcRS.C(1000,"MC.2016,TpcRS,StiCA,KFVertex,rung.'${rung}'",0,"heed,PhysicsOff,'${file}'")' >& ${logFile}
#    root4star -q -b 'TpcRS.C(1000,"MC.2016,sdt20160419.051230,TpcRS,StiCA,KFVertex,rung.'${rung}'",0,"heed,PhysicsOff,'${file}'")' >& ${logFile}
  endif
