  if ( -r ${SUBMITTINGDIRECTORY}/$logFile) exit 0;
  set muFile = ${filel}.MuDst.root
  if (-r ${SUBMITTINGDIRECTORY}/$muFile) exit 0;
  set bla = ${SUBMITTINGDIRECTORY}/${filel}.bla.root
  if (-r ${bla}) exit 0;
  touch ${bla}
