job.command:
         cd ${SUBMITTINGDIRECTORY}
   setenv StarEndMakerShell
   setenv STARFPE NO
   setenv NODEBUG yes
   setup  gcc631
   setup 64b
   starver TFG19c
     set evtsFile = $INPUTFILE0;
     set f        = 1
     set l        = 5000000
  echo "${evtsFile}"
  set filel    = `basename $evtsFile .daq`
  set output  = ${filel}.event.root
  if (! -r ${evtsFile}) exit 1;
  set logFile  = ${filel}B.log
  if ( -r $logFile) exit 0;
  set muFile = ${filel}.MuDst.root
  if (-r $muFile) exit 0;
# TFG
   root.exe -q -b 'bfc.C(1000,"P2018a,-hitfilt,mtd,btof,BEmcChkStat,CorrX,-Opr13,OPr40,OSpaceZ2,OGridLeak3D,evout,NoHistos,noTags,noRunco,Stx,KFVertex,evout,PicoVtxDefault","'${evtsFile}'")' >& ${logFile}
