foreach input (`cat $FILELIST`)
  set picoDir = ${input};
  set run = `basename ${picoDir}`;
  set logb = ${run}B.log
  set log  = ${SUBMITTINGDIRECTORY}/${logb}
  if (-r ${log}) exit 0;
  set outputb = ${run}.root;
  set output  =  ${SUBMITTINGDIRECTORY}/${outputb};
  if (-r ${output}) exit 0;
  cp -p ${SUBMITTINGDIRECTORY}/${picoDir}/*picoDst.root .
  root.exe -q -b -x  'kfpAnalysis.C(1000000,"*picoDst.root","'${outputb}'","y2019")' >& ${logb}
  mv ${outputb} ${output}
  mv ${logb} ${log}
end
