foreach input (`cat $FILELIST`)
  set pico = ${input};
  set picoB = `basename ${pico} .picoDst.root`;
  set logb = ${picoB}B.log
  set log  = ${SUBMITTINGDIRECTORY}/${logb}
  if (-r ${log}) exit 0;
  set outputb = ${picoB}.root;
  set output  =  ${SUBMITTINGDIRECTORY}/${outputb};
  if (-r ${output}) exit 0;
  cp -p ${SUBMITTINGDIRECTORY}/${input} .
  root.exe -q -b -x  'kfpAnalysis.C(1000000,"'${picoB}'.picoDst.root","'${outputb}'","y2020")' >& ${logb}
  mv ${outputb} ${output}
  mv ${logb} ${log}
end
