job.command:
cd ${SUBMITTINGDIRECTORY}
setenv STARFPE NO
set l        = 10000000
set evtsFile = $INPUTFILE0;
echo "${evtsFile}"
if (! -r ${evtsFile}) exit 1;
set fileb    = `basename $evtsFile`
set filel    = `basename $evtsFile .tree.root`
set output  = ${filel}.plot.root
if ( -r $output) exit 0;
set logFile  = ${filel}.plotB.log
if ( -r $logFile) exit 0;
root.exe -q -b 'makeHftPlots.C("'${fileb}'")' >& ${logFile}
