job.command:
       cd ${SUBMITTINGDIRECTORY}
set evtsFile = $INPUTFILE0;
set file     = `basename $evtsFile _200evts_Wplus_enu.fzd` #_4000evts_minb
set dir      = `dirname  $evtsFile`;          
echo "${evtsFile}"
if (! -r ${evtsFile}) exit 1;
set logFile  = ${file}B.log
if ( -r $logFile) exit 0;
set fitter = "KFVertex"
set case = `basename $ddir`; echo "case = $case";
echo $case | grep PPV
if (! $?) set fitter = "";
echo "fitter $fitter"
 root4star -q -b 'bfc.C(9999,"MC.y2011,fzin,'${fitter}',beamline,StiCA,NoHistos,NoRunco,-EvOut","'${evtsFile}'")' >& ${logFile}
