job.command:
cd ${SUBMITTINGDIRECTORY}
set evtsFile = $INPUTFILE0;
set file     = `basename $evtsFile .nt` #_4000evts_minb
set tfile    = `echo $file | sed -e 's/\./_/g'`
set nfile    = ${tfile}.nt
ln -s ${evtsFile} ${nfile}
echo "${evtsFile}"
if (! -r ${evtsFile}) exit 1;
set logFile  = ${tfile}B.log
if ( -r $logFile) exit 0;
set fitter = "KFVertex,StiCA,HLTCA"
set case = `basename $PWD`; echo "case = $case";
echo $case | grep VFM
if (! $?) set fitter = "VFMinuit,Sti";
echo "fitter $fitter"
root4star -q -b 'bfc.C(100,"MC.y2011,dbV20111124,ntin,'${fitter}',NoHistos,NoRunco,geantOut,EvOut","'${nfile}'")' >&  ${logFile}
